  <span class="token punctuation">}</span><span class="token punctuation">,</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token keyword">const</span> emit <span class="token operator">=</span> <span class="token function">defineEmits</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token string">"toggle"</span><span class="token punctuation">,</span> <span class="token string">"remove"</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

